mapper.select:
SELECT

FROM click_info
WHERE
LENGTH(code) = 6

    AND code = #{code, jdbcType=VARCHAR}


    AND sendtime >= #{begin, jdbcType=BIGINT}


    AND sendtime <= #{end, jdbcType=BIGINT}

GROUP BY code,sendtime

SELECT

FROM click_info
WHERE
LENGTH(code) = 6

    AND code = #{code, jdbcType=VARCHAR}


    AND sendtime >= #{begin, jdbcType=BIGINT}


    AND sendtime <= #{end, jdbcType=BIGINT}

GROUP BY code

SELECT

FROM click_info


        code = #{code}


        AND created >= #{begin, jdbcType=BIGINT}


        AND created <= #{end, jdbcType=BIGINT}


GROUP BY ip, code
ORDER BY created ASC

SELECT

FROM click_info


        code = #{code}


        AND created >= #{begin, jdbcType=BIGINT}


        AND created <= #{end, jdbcType=BIGINT}


ORDER BY created ASC

SELECT

FROM click_info


        code = #{code,jdbcType=VARCHAR}


        AND type = #{type,jdbcType=VARCHAR}


        AND code LIKE #{keyword}"%"


        AND browser = #{browser,jdbcType=VARCHAR}


        AND created < #{endtime,jdbcType=TIMESTAMP}


        AND created > #{starttime,jdbcType=TIMESTAMP}


ORDER BY created DESC
LIMIT #{offset},#{limit}

SELECT COUNT(id) AS total ,ip FROM click_info
where ip LIKE #{keyword}"%"
GROUP BY ip LIMIT #{offset},#{limit}

select
COUNT(id)
from click_info


        code = #{code,jdbcType=VARCHAR}


        AND type = #{type,jdbcType=VARCHAR}


        AND code LIKE #{keyword}"%"


        AND browser = #{browser,jdbcType=VARCHAR}


        AND created < #{endtime,jdbcType=TIMESTAMP}


        AND created > #{starttime,jdbcType=TIMESTAMP}

mapper.insert:
insert into click_info (id, code, type, 
